import React, { useState } from 'react';

function App() {
  const [recipients, setRecipients] = useState('');
  const [prompt, setPrompt] = useState('');
  const [generatedEmail, setGeneratedEmail] = useState('');
  const [subject, setSubject] = useState('');

  // 1. Generate email from LLM
  const handleGenerate = async () => {
    try {
      if (!prompt) {
        alert("Please provide a prompt.");
        return;
      }

      const response = await fetch('/api/generate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ prompt })
      });

      const data = await response.json();
      if (data.error) {
        alert(data.error);
      } else {
        setGeneratedEmail(data.emailContent);
      }
    } catch (error) {
      console.error('Error generating email:', error);
      alert('Error generating email content.');
    }
  };

  // 2. Send email via the backend
  const handleSend = async () => {
    try {
      if (!recipients || !generatedEmail) {
        alert("Recipient(s) and email content are required to send.");
        return;
      }

      const response = await fetch('/api/send', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          recipients,
          subject: subject || 'AI-Generated Email',
          emailBody: generatedEmail
        })
      });

      const data = await response.json();
      if (data.error) {
        alert(data.error);
      } else {
        alert('Email sent successfully!');
      }
    } catch (error) {
      console.error('Error sending email:', error);
      alert('Error sending email.');
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '50px auto', fontFamily: 'sans-serif' }}>
      <h1>Email Generation App</h1>

      <div style={{ marginBottom: '20px' }}>
        <label>Recipients (comma-separated):</label>
        <input
          type="text"
          style={{ width: '100%', padding: '8px', marginTop: '5px' }}
          value={recipients}
          onChange={(e) => setRecipients(e.target.value)}
          placeholder="e.g. test1@gmail.com, test2@gmail.com"
        />
      </div>

      <div style={{ marginBottom: '20px' }}>
        <label>Prompt:</label>
        <textarea
          rows="4"
          style={{ width: '100%', padding: '8px', marginTop: '5px' }}
          value={prompt}
          onChange={(e) => setPrompt(e.target.value)}
          placeholder="Describe the email context..."
        />
      </div>

      <button onClick={handleGenerate}>Generate Email</button>

      {generatedEmail && (
        <div style={{ marginTop: '30px' }}>
          <div style={{ marginBottom: '20px' }}>
            <label>Subject (optional):</label>
            <input
              type="text"
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              value={subject}
              onChange={(e) => setSubject(e.target.value)}
              placeholder="Optional Subject"
            />
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label>Generated Email:</label>
            <textarea
              rows="8"
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
              value={generatedEmail}
              onChange={(e) => setGeneratedEmail(e.target.value)}
            />
          </div>

          <button onClick={handleSend}>Send Email</button>
        </div>
      )}
    </div>
  );
}

export default App;
